/* non-standard, but extremely useful -- more so than all the *-ci stuff put together!
 */
static s7_pointer g_char_position(s7_scheme *sc, s7_pointer args)
{
  #define H_char_position "(char-position char-or-str str (start 0)) returns the position of the first occurrence of char in str, or #f"
  const char *porig, *p, *pset;
  char c;
  s7_Int start = 0, pos, len; /* not "int" because start arg might be most-negative-fixnum */
  s7_pointer arg1, arg2;

  arg1 = car(args);
  arg2 = cadr(args);

  if (!is_string(arg2))
    return(wrong_type_argument(sc, sc->CHAR_POSITION, small_int(2), arg2, T_STRING));
  porig = string_value(arg2);

  if (is_pair(cddr(args)))
    {
      s7_pointer arg3;
      arg3 = caddr(args);
      if (!s7_is_integer(arg3))
	return(wrong_type_argument(sc, sc->CHAR_POSITION, small_int(3), arg3, T_INTEGER));

      start = s7_integer(arg3);
      if (start < 0)
	return(wrong_type_argument_with_type(sc, sc->CHAR_POSITION, small_int(3), arg3, A_NON_NEGATIVE_INTEGER));
    }

  len = string_length(arg2);
  if ((!porig) || (start >= len))
      return(sc->F);

  if (s7_is_character(arg1))
    {
      c = character(arg1);
      p = strchr((const char *)(porig + start), (int)c); /* use strchrnul in Gnu C do catch embedded null case */
      if (p)
	return(make_integer(sc, p - porig));
      return(sc->F);
    }
  
  if (!is_string(arg1))
    return(wrong_type_argument(sc, sc->CHAR_POSITION, small_int(1), arg1, T_CHARACTER));

  if (string_length(arg1) == 0)
    return(sc->F);
  pset = string_value(arg1);

  pos = strcspn((const char *)(porig + start), (const char *)pset);
  if ((pos + start) < len)
    return(make_integer(sc, pos + start));

  /* but if the string has an embedded null, we can get erroneous results here --
   *   perhaps check for null at pos+start?  What about a searched-for string that
   *   also has embedded nulls?  
   *
   * The embedded nulls are for bytevector usages, where presumably you're not talking
   *   about chars and strings, so I think I'll ignore these cases.  In unicode, you'd
   *   want to use unicode-aware searchers, so that also is irrelevant.
   */
  return(sc->F);
}


static s7_pointer g_string_position(s7_scheme *sc, s7_pointer args)
{
  #define H_string_position "(string-position str1 str2 (start 0)) returns the starting position of str1 in str2 or #f"
  const char *s1, *s2, *p2;
  s7_Int start = 0;
  s7_pointer s1p, s2p;

  s1p = car(args);
  s2p = cadr(args);

  if (!is_string(s1p))
    return(wrong_type_argument(sc, sc->STRING_POSITION, small_int(1), s1p, T_STRING));
  if (!is_string(s2p))
    return(wrong_type_argument(sc, sc->STRING_POSITION, small_int(2), s2p, T_STRING));

  if (is_pair(cddr(args)))
    {
      s7_pointer arg3;
      arg3 = caddr(args);
      if (!s7_is_integer(arg3))
	return(wrong_type_argument(sc, sc->STRING_POSITION, small_int(3), arg3, T_INTEGER));

      start = s7_integer(arg3);
      if (start < 0)
	return(wrong_type_argument_with_type(sc, sc->STRING_POSITION, small_int(3), arg3, A_NON_NEGATIVE_INTEGER));
    }
  
  if (string_length(s1p) == 0)
    return(sc->F);
  s1 = string_value(s1p);
  s2 = string_value(s2p);
  if (start >= string_length(s2p))
    return(sc->F);

  p2 = strstr((const char *)(s2 + start), s1);
  if (!p2) return(sc->F);
  return(make_integer(sc, p2 - s2));
}





/* -------------------------------- strings -------------------------------- */

s7_pointer s7_make_string_with_length(s7_scheme *sc, const char *str, int len) 
{
  s7_pointer x;
  NEW_CELL(sc, x);
  set_type(x, T_STRING | T_SAFE_PROCEDURE); /* should this follow the malloc? */
  string_value(x) = (char *)malloc((len + 1) * sizeof(char)); 
  if (len != 0)                                             /* memcpy can segfault if string_value(x) is NULL */
    memcpy((void *)string_value(x), (void *)str, len + 1);
  else string_value(x)[0] = 0;
  string_length(x) = len;
  string_hash(x) = 0;
  string_needs_free(x) = 1;
  add_string(sc, x);
  return(x);
}


static s7_pointer s7_make_terminated_string_with_length(s7_scheme *sc, const char *str, int len) 
{
  s7_pointer x;
  NEW_CELL(sc, x);
  set_type(x, T_STRING | T_SAFE_PROCEDURE); /* should this follow the malloc? */
  string_value(x) = (char *)malloc((len + 1) * sizeof(char)); 
  if (len != 0)                                             /* memcpy can segfault if string_value(x) is NULL */
    memcpy((void *)string_value(x), (void *)str, len);
  string_value(x)[len] = 0;
  string_length(x) = len;
  string_hash(x) = 0;
  string_needs_free(x) = 1;
  add_string(sc, x);
  return(x);
}


static s7_pointer make_string_uncopied_with_length(s7_scheme *sc, char *str, int len) 
{
  s7_pointer x;
  NEW_CELL(sc, x);
  set_type(x, T_STRING | T_SAFE_PROCEDURE);
  string_value(x) = str;
  string_length(x) = len;
  string_hash(x) = 0;
  string_needs_free(x) = 1;
  add_string(sc, x);
  return(x);
}


static s7_pointer make_protected_string(s7_scheme *sc, const char *str)
{
  s7_pointer x;
  NEW_CELL(sc, x);
  set_type(x, T_STRING | T_IMMUTABLE | T_SAFE_PROCEDURE);
  string_value(x) = (char *)str;
  string_length(x) = safe_strlen(str);
  string_hash(x) = 0;
  string_needs_free(x) = 0;
  return(x);
}


static s7_pointer make_empty_string(s7_scheme *sc, int len, char fill) 
{
  /* a permanent nil_string object saved almost nothing */
  s7_pointer x;
  NEW_CELL(sc, x);
  set_type(x, T_STRING);
  
  if (fill == 0)
    string_value(x) = (char *)calloc((len + 1), sizeof(char));
  else
    {
      string_value(x) = (char *)malloc((len + 1) * sizeof(char));
      memset((void *)(string_value(x)), fill, len);
    }
  string_value(x)[len] = 0;
  string_hash(x) = 0;
  string_length(x) = len;
  string_needs_free(x) = 1;
  add_string(sc, x);
  return(x);
}


s7_pointer s7_make_string(s7_scheme *sc, const char *str) 
{
  if (str)
    return(s7_make_string_with_length(sc, str, safe_strlen(str)));
  return(make_empty_string(sc, 0, '\0'));
}


static s7_pointer make_string_uncopied(s7_scheme *sc, char *str) 
{
  return(make_string_uncopied_with_length(sc, str, safe_strlen(str)));
}


static char *make_permanent_string(const char *str)
{
  char *x;
  int len;
  len = safe_strlen(str);
  x = (char *)malloc((len + 1) * sizeof(char)); 
  memcpy((void *)x, (void *)str, len);
  x[len] = 0;
  return(x);
}


s7_pointer s7_make_permanent_string(const char *str) 
{
  /* for the symbol table which is never GC'd */
  s7_pointer x;
  x = alloc_pointer();
  heap_location(x) = NOT_IN_HEAP;
  set_type(x, T_STRING | T_IMMUTABLE);
  if (str)
    {
      unsigned int len;
      len = safe_strlen(str);
      string_length(x) = len;
      string_value(x) = (char *)malloc((len + 1) * sizeof(char)); 
      memcpy((void *)string_value(x), (void *)str, len);
      string_value(x)[len] = 0;
    }
  else 
    {
      string_value(x) = NULL;
      string_length(x) = 0;
    }
  string_hash(x) = 0;
  string_needs_free(x) = 0;
  return(x);
}


static s7_pointer make_temporary_string(s7_scheme *sc, const char *str, int len) 
{
  static s7_pointer tmp_str = NULL;
  static int tmp_str_size = -1;
  static char *tmp_str_chars = NULL;

  if (!tmp_str)
    {
      tmp_str = alloc_pointer();
      heap_location(tmp_str) = NOT_IN_HEAP;
      set_type(tmp_str, T_STRING | T_SAFE_PROCEDURE);
      string_hash(tmp_str) = 0;
      string_needs_free(tmp_str) = 0;
    }
  if (len >= tmp_str_size)
    {
      if (len == 0)
	tmp_str_size = 8;
      else tmp_str_size = 2 * len;
      if (!tmp_str_chars)
	tmp_str_chars = (char *)malloc(tmp_str_size * sizeof(char));
      else tmp_str_chars = (char *)realloc(tmp_str_chars, tmp_str_size * sizeof(char));
      string_value(tmp_str) = tmp_str_chars;
    }

  string_length(tmp_str) = len;
  if (len > 0)
    memmove((void *)tmp_str_chars, (void *)str, len); /* not memcpy because str might be a temp string (i.e. tmp_str_chars -> itself) */
  tmp_str_chars[len] = 0;
  return(tmp_str);
}


bool s7_is_string(s7_pointer p)
{
  return(is_string(p));
}


const char *s7_string(s7_pointer p) 
{ 
  return(string_value(p));
}


static s7_pointer g_is_string(s7_scheme *sc, s7_pointer args)
{
  #define H_is_string "(string? obj) returns #t if obj is a string"
  check_boolean_method(sc, car(args), is_string, sc->IS_STRING, args);
}


#define MAX_STRING_LENGTH 1073741824

static s7_pointer g_make_string(s7_scheme *sc, s7_pointer args)
{
  #define H_make_string "(make-string len (val #\\space)) makes a string of length len filled with the character val (default: space)"
  s7_pointer n;
  s7_Int len;
  char fill = ' ';
  
  n = car(args);
  if (!s7_is_integer(n))
    {
      check_method(sc, n, sc->MAKE_STRING, args);
      return(wrong_type_argument(sc, sc->MAKE_STRING, small_int(1), n, T_INTEGER));
    }

  len = s7_integer(n);
  if (len < 0)
    return(out_of_range(sc, sc->MAKE_STRING, small_int(1), n, "a non-negative integer"));
  if (len > MAX_STRING_LENGTH)
    return(out_of_range(sc, sc->MAKE_STRING, small_int(1), n, "a reasonable integer!"));

  if (is_not_null(cdr(args))) 
    {
      if (!s7_is_character(cadr(args)))
	{
	  check_method(sc, cadr(args), sc->MAKE_STRING, args);
	  return(wrong_type_argument(sc, sc->MAKE_STRING, small_int(2), cadr(args), T_CHARACTER));
	}
      fill = s7_character(cadr(args));
    }
  return(make_empty_string(sc, (int)len, fill));
}


static s7_pointer g_string_length(s7_scheme *sc, s7_pointer args)
{
  #define H_string_length "(string-length str) returns the length of the string str"
  s7_pointer p;
  p = car(args);
  if (!is_string(p))
    {
      check_method(sc, p, sc->STRING_LENGTH, args);
      return(simple_wrong_type_argument(sc, sc->STRING_LENGTH, p, T_STRING));
    }
  return(make_integer(sc, string_length(p)));
}


static s7_pointer g_string_downcase(s7_scheme *sc, s7_pointer args)
{
  #define H_string_downcase "(string-downcase str) returns the lower case version of str."
  s7_pointer p, newstr;
  int i, len;
  unsigned char *nstr, *ostr;

  p = car(args);
  if (!is_string(p))
    {
      check_method(sc, p, sc->STRING_DOWNCASE, args);
      return(simple_wrong_type_argument(sc, sc->STRING_DOWNCASE, p, T_STRING));
    }

  len = string_length(p);
  newstr = make_empty_string(sc, len + 1, 0); 
  string_length(newstr) = len;

  ostr = (unsigned char *)string_value(p);
  nstr = (unsigned char *)string_value(newstr);
  for (i = 0; i < len; i++)
    nstr[i] = tolower((int)ostr[i]);

  return(newstr);
}


static s7_pointer g_string_upcase(s7_scheme *sc, s7_pointer args)
{
  #define H_string_upcase "(string-upcase str) returns the upper case version of str."
  s7_pointer p, newstr;
  int i, len;
  unsigned char *nstr, *ostr;

  p = car(args);
  if (!is_string(p))
    {
      check_method(sc, p, sc->STRING_UPCASE, args);
      return(simple_wrong_type_argument(sc, sc->STRING_UPCASE, p, T_STRING));
    }

  len = string_length(p);
  newstr = make_empty_string(sc, len + 1, 0); 
  string_length(newstr) = len;

  ostr = (unsigned char *)string_value(p);
  nstr = (unsigned char *)string_value(newstr);
  for (i = 0; i < len; i++)
    nstr[i] = toupper((int)ostr[i]);

  return(newstr);
}


unsigned int s7_string_length(s7_pointer str)
{
  return(string_length(str));
}


static s7_pointer string_ref_1(s7_scheme *sc, s7_pointer strng, s7_pointer index)
{
  char *str;
  s7_Int ind;

  if (!s7_is_integer(index))
    return(wrong_type_argument(sc, sc->STRING_REF, small_int(2), index, T_INTEGER));
  ind = s7_integer(index);
  if (ind < 0)
    return(wrong_type_argument_with_type(sc, sc->STRING_REF, small_int(2), index, A_NON_NEGATIVE_INTEGER));
  if (ind >= string_length(strng))
    return(out_of_range(sc, sc->STRING_REF, small_int(2), index, "should be less than string length"));

  str = string_value(strng);
  return(s7_make_character(sc, ((unsigned char *)str)[ind]));
}


static s7_pointer g_string_ref(s7_scheme *sc, s7_pointer args)
{
  s7_pointer strng, index;
  char *str;
  s7_Int ind;

  #define H_string_ref "(string-ref str index) returns the character at the index-th element of the string str"

  strng = car(args);
  if (!is_string(strng))
    {
      check_method(sc, strng, sc->STRING_REF, args);
      return(wrong_type_argument(sc, sc->STRING_REF, small_int(1), strng, T_STRING));
    }
  
  index = cadr(args);
  if (!s7_is_integer(index))
    return(wrong_type_argument(sc, sc->STRING_REF, small_int(2), index, T_INTEGER));
  ind = s7_integer(index);
  if (ind < 0)
    return(wrong_type_argument_with_type(sc, sc->STRING_REF, small_int(2), index, A_NON_NEGATIVE_INTEGER));
  if (ind >= string_length(strng))
    return(out_of_range(sc, sc->STRING_REF, small_int(2), index, "should be less than string length"));

  str = string_value(strng);
  return(s7_make_character(sc, ((unsigned char *)str)[ind]));
}


static s7_pointer g_string_set(s7_scheme *sc, s7_pointer args)
{
  #define H_string_set "(string-set! str index chr) sets the index-th element of the string str to the character chr"
  
  s7_pointer x, c, index;
  char *str;
  s7_Int ind;

  x = car(args);
  if (!is_string(x))
    {
      check_method(sc, x, sc->STRING_SET, args);
      return(wrong_type_argument(sc, sc->STRING_SET, small_int(1), x, T_STRING));
    }

  index = cadr(args);
  if (!s7_is_integer(index))
    return(wrong_type_argument(sc, sc->STRING_SET, small_int(2), index, T_INTEGER));
  ind = s7_integer(index);
  if (ind < 0)
    return(wrong_type_argument_with_type(sc, sc->STRING_SET, small_int(2), index, A_NON_NEGATIVE_INTEGER));
  if (ind >= string_length(x))
    return(out_of_range(sc, sc->STRING_SET, small_int(2), index, "should be less than string length"));
  str = string_value(x);

  c = caddr(args);
  if (!s7_is_character(c))
    {
      if ((is_bytevector(x)) &&
	  (s7_is_integer(c)))
	{
	  int ic;
	  ic = s7_integer(c);
	  if ((ic < 0) || (ic > 255))
	    return(wrong_type_argument_with_type(sc, sc->STRING_SET, small_int(3), c, AN_UNSIGNED_BYTE));
	  str[ind] = (char)ic;
	  return(c);
	}
      check_method(sc, c, sc->STRING_SET, args);
      return(wrong_type_argument(sc, sc->STRING_SET, small_int(3), c, T_CHARACTER));
    }

  str[ind] = (char)s7_character(c);
  return(c);
}


static s7_pointer g_string_append_1(s7_scheme *sc, s7_pointer args, s7_pointer sym)
{
  int len = 0;
  s7_pointer x, newstr;
  char *pos;
  
  if (is_null(args))
    return(s7_make_string_with_length(sc, "", 0));
  
  /* get length for new string */
  for (x = args; is_not_null(x); x = cdr(x)) 
    {
      if (!is_string(car(x)))
	{
	  check_method(sc, car(x), sym, args);
	  return(wrong_type_argument_n(sc, sym, position_of(x, args), car(x), T_STRING));
	}
      len += string_length(car(x));
    }
  
  /* store the contents of the argument strings into the new string */
  newstr = make_empty_string(sc, len + 1, 0); 
  string_length(newstr) = len;
  for (pos = string_value(newstr), x = args; is_not_null(x); pos += string_length(car(x)), x = cdr(x)) 
    memcpy(pos, string_value(car(x)), string_length(car(x)));
  
  return(newstr);
}


static s7_pointer g_string_append(s7_scheme *sc, s7_pointer args)
{
  #define H_string_append "(string-append str1 ...) appends all its string arguments into one string"
  return(g_string_append_1(sc, args, sc->STRING_APPEND));
}


static s7_pointer g_string_copy(s7_scheme *sc, s7_pointer args)
{
  #define H_string_copy "(string-copy str) returns a copy of its string argument"
  return(g_string_append_1(sc, args, sc->STRING_COPY));
}


static bool start_and_end(s7_scheme *sc, s7_pointer caller, s7_pointer start_and_end_args, int position, s7_Int *start, s7_Int *end)
{
  /* we assume that *start=0 and *end=length, that end is "exclusive"
   *   return true if the start/end points are not changed.
   */
  s7_pointer pstart, pend;
  s7_Int index;

  if (is_null(start_and_end_args))
    return(true);

  pstart = car(start_and_end_args);
  if (!s7_is_integer(pstart))
    {
      wrong_type_argument_n(sc, caller, position, pstart, T_INTEGER);
      return(true);
    }
      
  index = s7_integer(pstart);
  if ((index < 0) ||
      (index > *end)) /* *end == length here */
    {
      out_of_range(sc, caller, small_int(position), pstart, "should be between 0 and length");
      return(true);
    }
  *start = index;

  if (is_null(cdr(start_and_end_args)))
    return(index == 0);

  pend = cadr(start_and_end_args);
  if (!s7_is_integer(pend))
    {
      wrong_type_argument_n(sc, caller, position + 1, pend, T_INTEGER);
      return(true);
    }
  index = s7_integer(pend);
  if ((index < *start) ||
      (index > *end))
    {
      out_of_range(sc, caller, small_int(position + 1), pend, "should be between the start index and the length");
      return(true);
    }
  
  if (index == *end)
    return(*start == 0);
  *end = index;
  
  return(false);
}


static s7_pointer g_substring(s7_scheme *sc, s7_pointer args)
{
  #define H_substring "(substring str start (end (length str))) returns the portion of the string str between start and \
end: (substring \"01234\" 1 2) -> \"1\""
  
  s7_pointer x, str;
  s7_Int start = 0, end;
  int len;
  char *s;
  
  str = car(args);
  if (!is_string(str))
    {
      check_method(sc, str, sc->SUBSTRING, args);
      return(wrong_type_argument(sc, sc->SUBSTRING, small_int(1), str, T_STRING));
    }

  end = string_length(str);
  start_and_end(sc, sc->SUBSTRING, cdr(args), 2, &start, &end);
  s = string_value(str);
  len = (int)(end - start);
  x = s7_make_string_with_length(sc, (char *)(s + start), len);
  string_value(x)[len] = 0;
  return(x);
}


static s7_pointer substring_to_temp;
static s7_pointer g_substring_to_temp(s7_scheme *sc, s7_pointer args)
{
  s7_pointer str;
  s7_Int start = 0, end;
  
  str = car(args);
  if (!is_string(str))
    {
      check_method(sc, str, sc->SUBSTRING, args);
      return(wrong_type_argument(sc, sc->SUBSTRING, small_int(1), str, T_STRING));
    }

  end = string_length(str);
  start_and_end(sc, sc->SUBSTRING, cdr(args), 2, &start, &end);

  return(make_temporary_string(sc, (const char *)(string_value(str) + start), (int)(end - start)));
}



/* (set! (substring...) ...)? -- might require allocation
 *   to implement substring_uncopied we'd need to make sure the lack of a trailing null can't confuse anyone
 *   this is also the case for read-line.
 *
 * (string=? (substring...) str) or reverse is very common 
 */


static use_write_t write_choice(s7_scheme *sc, s7_pointer arg)
{
  if (arg == sc->F) return(USE_DISPLAY);
  if (arg == sc->T) return(USE_WRITE);
  if (arg == sc->KEY_READABLE) return(USE_READABLE_WRITE);
  return(USE_WRITE_WRONG);
}

#define DONT_USE_DISPLAY(Choice) ((Choice == USE_DISPLAY) ? USE_WRITE : Choice)

static char *s7_object_to_c_string_1(s7_scheme *sc, s7_pointer obj, use_write_t use_write, int *nlen);

static s7_pointer g_object_to_string(s7_scheme *sc, s7_pointer args)
{
  #define H_object_to_string "(object->string obj (write #t)) returns a string representation of obj."
  use_write_t choice = USE_WRITE;
  char *str;

  if (is_not_null(cdr(args)))
    {
      choice = write_choice(sc, cadr(args));
      if (choice == USE_WRITE_WRONG)
	return(wrong_type_argument(sc, sc->OBJECT_TO_STRING, small_int(2), cadr(args), T_BOOLEAN));
    }
  /* can't use s7_object_to_string here anymore because it assumes use_write arg is a boolean
   */
  str = s7_object_to_c_string_1(sc, car(args), choice, NULL);
  if (str)
    return(make_string_uncopied(sc, str));
  return(s7_make_string_with_length(sc, "", 0)); 
}


static int scheme_strcmp(s7_pointer s1, s7_pointer s2)
{
  /* tricky here because str[i] must be treated as unsigned
   *   (string<? (string (integer->char #xf0)) (string (integer->char #x70)))
   * also null or lack thereof does not say anything about the string end
   *   so we have to go by its length.
   */
  int i, len, len1, len2;
  char *str1, *str2;

  len1 = string_length(s1);
  len2 = string_length(s2);
  if (len1 > len2)
    len = len2;
  else len = len1;

  str1 = string_value(s1);
  str2 = string_value(s2);

  for (i = 0; i < len; i++)
    if ((unsigned char)(str1[i]) < (unsigned char )(str2[i]))
      return(-1);
    else
      {
	if ((unsigned char)(str1[i]) > (unsigned char)(str2[i]))
	  return(1);
      }

  if (len1 < len2) 
    return(-1);
  if (len1 > len2)
    return(1);
  return(0);
}


static s7_pointer g_string_cmp(s7_scheme *sc, s7_pointer args, int val, s7_pointer sym)
{
  s7_pointer x, y;
  
  for (x = args; is_not_null(x); x = cdr(x))  
    if (!is_string(car(x)))
      {
	check_method(sc, car(x), sym, args);
	return(wrong_type_argument_n(sc, sym, position_of(x, args), car(x), T_STRING));
      }
  y = car(args);
  for (x = cdr(args); is_not_null(x); x = cdr(x))
    {
      if (scheme_strcmp(y, car(x)) != val)
	return(sc->F);
      y = car(x);
    }
  return(sc->T);
}


static s7_pointer g_string_cmp_not(s7_scheme *sc, s7_pointer args, int val, s7_pointer sym)
{
  s7_pointer x, y;
  
  for (x = args; is_not_null(x); x = cdr(x))  
    if (!is_string(car(x)))
      {
	check_method(sc, car(x), sym, args);
	return(wrong_type_argument_n(sc, sym, position_of(x, args), car(x), T_STRING));
      }
  y = car(args);
  for (x = cdr(args); is_not_null(x); x = cdr(x))
    {
      if (scheme_strcmp(y, car(x)) == val)
	return(sc->F);
      y = car(x);
    }
  return(sc->T);
}


static bool scheme_strings_are_equal(s7_pointer x, s7_pointer y)
{
  char *xstr, *ystr;
  unsigned int i, len;

  len = string_length(x);
  if (string_length(y) != len) 
    return(false);

  xstr = string_value(x);
  ystr = string_value(y);

  for (i = 0; i < len; i++)
    if (xstr[i] != ystr[i])
      return(false);
  return(true);
}


static s7_pointer g_strings_are_equal(s7_scheme *sc, s7_pointer args)
{
  #define H_strings_are_equal "(string=? str ...) returns #t if all the string arguments are equal"

  /* C-based check stops at null, but we can have embedded nulls.
   *   (let ((s1 "1234") (s2 "1245")) (string-set! s1 1 #\null) (string-set! s2 1 #\null) (string=? s1 s2))
   */
  
  s7_pointer x, y;
  unsigned int len;
  bool happy = true;
  char *ystr;
  
  y = car(args);
  if (!is_string(y))
    {
      check_method(sc, y, sc->STRING_EQ, args);
      return(wrong_type_argument(sc, sc->STRING_EQ, small_int(1), y, T_STRING));
    }
  len = string_length(y);
  ystr = string_value(y);

  for (x = cdr(args); is_not_null(x); x = cdr(x)) 
    {
      if (y != car(x))
	{
	  if (!is_string(car(x)))
	    {
	      check_method(sc, x, sc->STRING_EQ, args);
	      return(wrong_type_argument_n(sc, sc->STRING_EQ, position_of(x, args), car(x), T_STRING));
	    }
	  if (happy)
	    {
	      if (string_length(car(x)) != len)
		happy = false;
	      else
		{
		  unsigned int k;
		  char *xstr;
		  xstr = string_value(car(x));
		  for (k = 0; k < len; k++)
		    if (ystr[k] != xstr[k])
		      {
			happy = false;
			break;
		      }
		}
	    }
	}
    }

  if (!happy)
    return(sc->F);
  return(sc->T);
}	


