static s7_pointer g_string_fill(s7_scheme *sc, s7_pointer args)
{
  #define H_string_fill "(string-fill! str chr start end) fills the string str with the character chr"
  s7_pointer x, chr;
  s7_Int start = 0, end;
  x = car(args);

  if (!is_string(x))
    {
      check_method(sc, x, sc->STRING_FILL, args);
      return(wrong_type_argument(sc, sc->STRING_FILL, small_int(1), x, T_STRING));
    }

  chr = cadr(args);
  if (!s7_is_character(chr))
    {
      check_method(sc, chr, sc->STRING_FILL, args);
      return(wrong_type_argument(sc, sc->STRING_FILL, small_int(2), chr, T_CHARACTER));
    }
  /* strlen and so on here is probably not right -- a scheme string has a length
   *   set when it is created, and (apparently) can contain an embedded 0, so its
   *   print length is not its length.
   *         char *str; char c; str = string_value(car(args)); c = character(cadr(args));
   *         int i, len = 0; if (str) len = safe_strlen(str); if (len > 0) for (i = 0; i < len; i++) str[i] = c; 
   */
  end = string_length(x);
  if (end == 0) return(chr);

  if (!is_null(cddr(args)))
    {
      start_and_end(sc, sc->STRING_FILL, cddr(args), 3, &start, &end);
      if (start == end) return(chr);
    }
  memset((void *)(string_value(x) + start), (int)character(chr), end - start);
  return(chr);
}


static s7_pointer g_string_1(s7_scheme *sc, s7_pointer args, s7_pointer sym)
{
  int i, len;
  s7_pointer x, newstr;
  
  /* get length for new string and check arg types */
  for (len = 0, x = args; is_not_null(x); len++, x = cdr(x)) 
    if (!s7_is_character(car(x)))
      {
	check_method(sc, car(x), sym, args);
	return(wrong_type_argument_n(sc, sym, len + 1, car(x), T_CHARACTER));
      }
  newstr = make_empty_string(sc, len, 0);
  for (i = 0, x = args; is_not_null(x); i++, x = cdr(x)) 
    string_value(newstr)[i] = character(car(x));
  
  return(newstr);
}


static s7_pointer g_string(s7_scheme *sc, s7_pointer args)
{
  #define H_string "(string chr...) appends all its character arguments into one string"
  if (is_null(args))                                /* (string) but not (string ()) */
    return(s7_make_string_with_length(sc, "", 0));
  return(g_string_1(sc, args, sc->STRING));
}


static s7_pointer g_list_to_string(s7_scheme *sc, s7_pointer args)
{
  #define H_list_to_string "(list->string lst) appends all the list's characters into one string; (apply string lst)"
  if (is_null(car(args)))
    return(s7_make_string_with_length(sc, "", 0));
  
  if (!is_proper_list(sc, car(args)))
    {
      check_method(sc, car(args), sc->LIST_TO_STRING, args);
      return(simple_wrong_type_argument_with_type(sc, sc->LIST_TO_STRING, car(args), make_protected_string(sc, "a (proper, non-circular) list of characters")));
    }
  return(g_string_1(sc, car(args), sc->LIST_TO_STRING));
}


static s7_pointer s7_string_to_list(s7_scheme *sc, const char *str, int len)
{
  int i;
  s7_pointer p;

  if (len == 0)
    return(sc->NIL);

  if (len < (sc->free_heap_top - sc->free_heap))
    {
      sc->w = s7_cons(sc, s7_make_character(sc, ((unsigned char)str[len - 1])), sc->NIL);
      for (i = len - 2; i >= 0; i--)
	sc->w = cons_unchecked(sc, s7_make_character(sc, ((unsigned char)str[i])), sc->w);
    }
  else
    {
      sc->w = sc->NIL;
      for (i = len - 1; i >= 0; i--)
	sc->w = cons(sc, s7_make_character(sc, ((unsigned char)str[i])), sc->w);
    }
  p = sc->w;
  sc->w = sc->NIL;
  return(p);
}


static s7_pointer g_string_to_list(s7_scheme *sc, s7_pointer args)
{
  #define H_string_to_list "(string->list str start end) returns the elements of the string str in a list; (map values str)"
  s7_Int i, start = 0, end;
  s7_pointer p, str;

  str = car(args);
  if (!is_string(str))
    {
      check_method(sc, str, sc->STRING_TO_LIST, args);
      return(simple_wrong_type_argument(sc, sc->STRING_TO_LIST, str, T_STRING));
    }

  end = string_length(str);

  if (!is_null(cdr(args)))
    {
      start_and_end(sc, sc->STRING_TO_LIST, cdr(args), 2, &start, &end);
      if (start == end) return(sc->NIL);
    }
  else
    {
      if (end == 0) return(sc->NIL);
    }
  if ((start == 0) && (end == string_length(str)))
    return(s7_string_to_list(sc, string_value(str), string_length(str)));
  
  sc->w = sc->NIL;
  for (i = end - 1; i >= start; i--)
    sc->w = cons(sc, s7_make_character(sc, ((unsigned char)string_value(str)[i])), sc->w);

  p = sc->w;
  sc->w = sc->NIL;
  return(p);
}


/* -------------------------------- bytevectors -------------------------------- 
 *
 * these are just strings with the T_BYTEVECTOR bit set.
 */

static s7_pointer g_is_bytevector(s7_scheme *sc, s7_pointer args)
{
  #define H_is_bytevector "(bytevector? obj) returns #t if obj is a bytevector"
  return(make_boolean(sc, is_string(car(args)) && is_bytevector(car(args))));
}


static s7_pointer g_to_bytevector(s7_scheme *sc, s7_pointer args)
{
  #define H_to_bytevector "(->bytevector obj) turns a string into a bytevector."
  if (!is_string(car(args)))
    return(wrong_type_argument(sc, sc->TO_BYTEVECTOR, small_int(1), car(args), T_STRING));
  set_bytevector(car(args));
  return(car(args));
}


static s7_pointer g_make_bytevector(s7_scheme *sc, s7_pointer args)
{
  #define H_make_bytevector "(make-bytevector len (byte 0)) makes a bytevector of length len filled with byte."
  s7_pointer str;
  if (is_null(cdr(args)))
    {
      str = g_make_string(sc, args);
      memset((void *)(string_value(str)), 0, string_length(str));
    }
  else 
    {
      s7_pointer byte;
      s7_Int b;
      byte = cadr(args);
      if (!s7_is_integer(byte))
	return(wrong_type_argument(sc, sc->MAKE_BYTEVECTOR, small_int(2), byte, T_INTEGER));
      b = s7_integer(byte);
      if ((b < 0) || (b > 255))
	return(simple_wrong_type_argument_with_type(sc, sc->MAKE_BYTEVECTOR, byte, AN_UNSIGNED_BYTE));
      str = g_make_string(sc, list_2(sc, car(args), chars[b]));
    }
  set_bytevector(str);
  return(str);
}


static s7_pointer g_bytevector(s7_scheme *sc, s7_pointer args)
{
  #define H_bytevector "(bytevector ...) returns a bytevector whose elements are the arguments"
  s7_Int i, len;
  s7_pointer vec, x;
  
  len = s7_list_length(sc, args);
  vec = make_empty_string(sc, len, 0);

  for (i = 0, x = args; is_pair(x); i++, x = cdr(x)) 
    {
      s7_pointer byte;
      s7_Int b;
      byte = car(x);
      if (!s7_is_integer(byte))
	return(wrong_type_argument(sc, sc->BYTEVECTOR, make_integer(sc, i), byte, T_INTEGER));
      b = s7_integer(byte);
      if ((b < 0) || (b > 255))
	return(simple_wrong_type_argument_with_type(sc, sc->BYTEVECTOR, byte, AN_UNSIGNED_BYTE));
      string_value(vec)[i] = (unsigned char)b;
    }
  set_bytevector(vec);
  return(vec);
}



   


/* -------------------------------- ports -------------------------------- 
 *
 * originally nil served as stdin and friends, but that made it impossible to catch an error
 *   like (read-line (current-output-port)) when the latter was stdout.  So we now have
 *   the built-in constant ports *stdin*, *stdout*, and *stderr*.  Some way is needed to
 *   refer to these directly so that (read-line *stdin*) for example can insist on reading
 *   from the terminal, or whatever stdin is.
 */

static s7_pointer g_is_port_closed(s7_scheme *sc, s7_pointer args)
{
  #define H_is_port_closed "(port-closed? p) returns #t if the port p is closed."
  s7_pointer x;

  x = car(args);
  if ((is_input_port(x)) || (is_output_port(x)))
    return(make_boolean(sc, port_is_closed(x)));

  check_method(sc, x, sc->IS_PORT_CLOSED, args);
  return(simple_wrong_type_argument_with_type(sc, sc->IS_PORT_CLOSED, x, make_protected_string(sc, "a port")));      
}


static s7_pointer g_port_line_number(s7_scheme *sc, s7_pointer args)
{
  #define H_port_line_number "(port-line-number input-file-port) returns the current read line number of port"
  s7_pointer x;

  if (is_null(args))
    x = sc->input_port;
  else x = car(args);

  if ((!(is_input_port(x))) ||
      (port_is_closed(x)))
    {
      check_method(sc, x, sc->PORT_LINE_NUMBER, args);
      return(simple_wrong_type_argument_with_type(sc, sc->PORT_LINE_NUMBER, x, AN_INPUT_PORT));
    }
  return(make_integer(sc, port_line_number(x)));
}


int s7_port_line_number(s7_pointer p)
{
  if (is_input_port(p))
    return(port_line_number(p));
  return(0);
}


const char *s7_port_filename(s7_pointer x)
{
  if (((is_input_port(x)) || 
       (is_output_port(x))) &&
      (!port_is_closed(x)))
    return(port_filename(x));
  return(NULL);
}


static s7_pointer g_port_filename(s7_scheme *sc, s7_pointer args)
{
  #define H_port_filename "(port-filename file-port) returns the filename associated with port"
  s7_pointer x;

  if (is_null(args))
    x = sc->input_port;
  else x = car(args);

  if (((is_input_port(x)) ||
       (is_output_port(x))) &&
      (!port_is_closed(x)))
    {
      if (port_filename(x))
	return(make_protected_string(sc, port_filename(x)));
      return(s7_make_string_with_length(sc, "", 0));   
      /* otherwise (eval-string (port-filename)) and (string->symbol (port-filename)) segfault */
    }
  check_method(sc, x, sc->PORT_FILENAME, args);
  return(simple_wrong_type_argument_with_type(sc, sc->PORT_FILENAME, x, AN_OPEN_PORT));
}


