#define FILLED true
#define NOT_FILLED false

static s7_pointer make_vector_1(s7_scheme *sc, s7_Int len, bool filled, int typ) 
{
  s7_pointer x;
  if (len < 0)
    return(wrong_type_argument_with_type(sc, sc->MAKE_VECTOR, small_int(1), make_integer(sc, len), A_NON_NEGATIVE_INTEGER));
  if (len > 134217728)
    {
      /* len is an "int" currently */
      float ilog2;

      ilog2 = log((double)len) / log(2.0);
      if (sizeof(size_t) > 4)
	{
	  if (ilog2 > 56.0)
	    return(out_of_range(sc, sc->MAKE_VECTOR, small_int(1), make_integer(sc, len), "should be less than about 2^56 probably"));
	}
      else
	{
	  if (ilog2 > 28.0)
	    return(out_of_range(sc, sc->MAKE_VECTOR, small_int(1), make_integer(sc, len), "should be less than about 2^28 probably"));
	}
    }

  /* if (len == 0) typ = T_VECTOR; *//* all empty vectors are the same type */
  /* this produces odd results: (float-vector? (float-vector)) -> #f etc 
   *   but keeping the type means equal? has to be smarter about empty vectors.
   */

  /* this has to follow the error checks!  (else garbage in free_heap temps portion confuses GC when "vector" is finalized) */
  NEW_CELL(sc, x);                     
  vector_length(x) = 0;
  vector_elements(x) = NULL;
  set_type(x, typ | T_SAFE_PROCEDURE); /* (v 0) as vector-ref is safe */

  if (len > 0)
    {
      vector_length(x) = len;
      if (typ == T_VECTOR)
	{
	  vector_elements(x) = (s7_pointer *)malloc(len * sizeof(s7_pointer));
	  vector_getter(x) = default_vector_getter;
	  vector_setter(x) = default_vector_setter;
	  /* make_hash_table assumes nil as the default value */
	  if (filled) s7_vector_fill(sc, x, sc->NIL);
	}
      else
	{
	  if (typ == T_INT_VECTOR)
	    {
	      if (filled)
		int_vector_elements(x) = (s7_Int *)calloc(len, sizeof(s7_Int));
	      else int_vector_elements(x) = (s7_Int *)malloc(len * sizeof(s7_Int));
	      vector_getter(x) = int_vector_getter;
	      vector_setter(x) = int_vector_setter;
	    }
	  else 
	    {
	      if (filled)
		float_vector_elements(x) = (s7_Double *)calloc(len, sizeof(s7_Double));
	      else float_vector_elements(x) = (s7_Double *)malloc(len * sizeof(s7_Double));
	      vector_getter(x) = float_vector_getter;
	      vector_setter(x) = float_vector_setter;
	    }
	}
      if (!(vector_elements(x)))
	return(s7_error(sc, make_symbol(sc, "out-of-memory"), 
			list_1(sc, make_protected_string(sc, "make-vector allocation failed!"))));
    }

  vector_dimension_info(x) = NULL;
  add_vector(sc, x);
  return(x);
}


s7_pointer s7_make_vector(s7_scheme *sc, s7_Int len)
{
  return(make_vector_1(sc, len, FILLED, T_VECTOR));
}


static s7_vdims_t *make_vdims(s7_scheme *sc, bool elements_allocated, int dims, s7_Int *dim_info)
{
  s7_vdims_t *v;
  int i;
  s7_Int offset = 1;

  v = (s7_vdims_t *)malloc(sizeof(s7_vdims_t));
  v->ndims = dims;
  v->elements_allocated = elements_allocated;
  v->dimensions_allocated = true;
  v->original = sc->F;
  v->dims = (s7_Int *)malloc(v->ndims * sizeof(s7_Int));
  v->offsets = (s7_Int *)malloc(v->ndims * sizeof(s7_Int));

  for (i = 0; i < dims; i++)
    v->dims[i] = dim_info[i];
  for (i = v->ndims - 1; i >= 0; i--)
    {
      v->offsets[i] = offset;
      offset *= v->dims[i];
    }
  
  return(v);
}


s7_pointer s7_make_int_vector(s7_scheme *sc, s7_Int len, int dims, s7_Int *dim_info)
{
  s7_pointer p;
  p = make_vector_1(sc, len, FILLED, T_INT_VECTOR);
  if (dim_info)
    vector_dimension_info(p) = make_vdims(sc, true, dims, dim_info);
  return(p);
}


s7_pointer s7_make_float_vector(s7_scheme *sc, s7_Int len, int dims, s7_Int *dim_info)
{
  s7_pointer p;
  p = make_vector_1(sc, len, FILLED, T_FLOAT_VECTOR);
  if (dim_info)
    vector_dimension_info(p) = make_vdims(sc, true, dims, dim_info);
  return(p);
}


s7_pointer s7_make_float_vector_wrapper(s7_scheme *sc, s7_Int len, s7_Double *data, int dims, s7_Int *dim_info, bool free_data)
{
  /* this wraps up a C-allocated/freed double array as an s7 vector.
   */
  s7_pointer x;

  NEW_CELL(sc, x);                     
  set_type(x, T_FLOAT_VECTOR | T_SAFE_PROCEDURE);
  float_vector_elements(x) = data;
  vector_getter(x) = float_vector_getter;
  vector_setter(x) = float_vector_setter;
  vector_length(x) = len;
  if (dim_info)
    vector_dimension_info(x) = make_vdims(sc, free_data, dims, dim_info);
  else vector_dimension_info(x) = NULL;     /* data won't be freed */
  add_vector(sc, x);

  return(x);
}


