/* -------- sort! -------- */

#if WITH_GCC

#define c_function_is_ok(Sc, X) \
  ({ s7_pointer _X_;  _X_ = X;						\
    ((ecdr(_X_)) &&							\
     ({s7_pointer _p_; _p_ = SYMBOL_TO_VALUE(Sc, car(_X_));		\
       ((_p_ == ecdr(_X_)) ||						\
	((is_any_c_function(_p_)) && (is_any_c_function(ecdr(_X_))) &&		\
	 (c_function_class(_p_) == c_function_class(ecdr(_X_))))); })); })
#else

static bool c_function_is_ok(s7_scheme *sc, s7_pointer x) 
{
  if (ecdr(x))
    {
      s7_pointer p;
      p = SYMBOL_TO_VALUE(sc, car(x));
      return((p == ecdr(x)) ||
	     ((is_any_c_function(p)) &&
	      (c_function_class(p) == c_function_class(ecdr(x)))));
    }
  return(false);
}
#endif


static bool arglist_has_accessed_symbol(s7_pointer args)
{
  /* rest arg if any not checked */
  s7_pointer p;
  for (p = args; is_pair(p); p = cdr(p))
    if (((is_symbol(car(p))) && (symbol_has_accessor(car(p)))) ||
	((is_pair(car(p))) && (symbol_has_accessor(caar(p)))))
      return(true);
  return(false);
}


static bool arglist_has_keyword(s7_pointer args)
{
  s7_pointer p;
  for (p = args; is_pair(p); p = cdr(p))
    if (is_keyword(car(p)))
      return(true);
  return(false);
}


static s7_function end_dox_eval(s7_scheme *sc, s7_pointer code);
static s7_pointer end_dox_or_not_ic(s7_scheme *sc, s7_pointer code);
static s7_pointer end_dox_or_not_ss(s7_scheme *sc, s7_pointer code);

static void initialize_dox_end_vars(s7_scheme *sc, s7_pointer end)
{
  switch (optimize_data(end))
    {
    case HOP_SAFE_C_C:
      break;
      
    case HOP_SAFE_C_S:
    case HOP_SAFE_C_SC:
    case HOP_SAFE_C_S_opCq:
      environment_dox1(sc->envir) = find_symbol(sc, cadr(end));
      break;
      
    case HOP_SAFE_C_CS:
    case HOP_SAFE_C_QS:
      environment_dox1(sc->envir) = find_symbol(sc, caddr(end));
      break;
      
    case HOP_SAFE_C_SS:
      environment_dox1(sc->envir) = find_symbol(sc, cadr(end));
      environment_dox2(sc->envir) = find_symbol(sc, caddr(end));
      break;
      
    case HOP_SAFE_C_opSq_S:
      environment_dox1(sc->envir) = find_symbol(sc, cadr(cadr(end)));
      environment_dox2(sc->envir) = find_symbol(sc, caddr(end));
      break;
      
    case HOP_SAFE_C_S_opSq:
    case HOP_SAFE_C_S_opSCq:
    case HOP_SAFE_C_S_opSSq:
      environment_dox1(sc->envir) = find_symbol(sc, cadr(end));
      environment_dox2(sc->envir) = find_symbol(sc, ecdr(cdr(end)));
      break;

    case HOP_SAFE_C_opSq_opSq:
      environment_dox1(sc->envir) = find_symbol(sc, cadr(cadr(end)));
      environment_dox2(sc->envir) = find_symbol(sc, cadr(caddr(end)));
      break;
    }
}


static s7_scheme *compare_sc;
static s7_function compare_func;
static s7_pointer compare_args;

static int vector_compare(const void *v1, const void *v2)
{
  car(compare_args) = (*(s7_pointer *)v1);
  cadr(compare_args) = (*(s7_pointer *)v2);
  if (is_true(compare_sc, (*(compare_func))(compare_sc, compare_args)))
    return(-1);
  return(1);
}

static int dox_compare(const void *v1, const void *v2)
{
  /* here v1 and v2 are the lambda args.  We need to place these in the current env slots,
   *   then call compare_func with the expression as its argument. So compare_args in this
   *   case is expr.
   */
  slot_set_value(environment_slots(compare_sc->envir), (*(s7_pointer *)v1));
  slot_set_value(next_slot(environment_slots(compare_sc->envir)), (*(s7_pointer *)v2));
  if (is_true(compare_sc, (*(compare_func))(compare_sc, compare_args)))
    return(-1);
  return(1);
}


static s7_pointer g_sort(s7_scheme *sc, s7_pointer args)
{
  #define H_sort "(sort! sequence less?) sorts a sequence using the function 'less?' to compare elements."

  s7_pointer data, lessp;
  s7_Int len = 0, n, k;
  int (*sort_func)(const void *v1, const void *v2);
  s7_pointer *elements;

  data = car(args);
  if (is_null(data)) 
    {
      /* (apply sort! () #f) should be an error I think 
       */
      lessp = cadr(args);
      if ((!is_procedure(lessp)) ||
	  (!s7_is_aritable(sc, lessp, 2)))
	return(wrong_type_argument_with_type(sc, sc->SORT, small_int(2), lessp, make_protected_string(sc, "a function of 2 arguments")));
      return(sc->NIL);
    }

  lessp = cadr(args);
  if (!is_procedure(lessp))
    return(wrong_type_argument_with_type(sc, sc->SORT, small_int(2), lessp, A_CLOSURE));
  if ((is_continuation(lessp)) || is_goto(lessp))
    return(wrong_type_argument_with_type(sc, sc->SORT, small_int(2), lessp, make_protected_string(sc, "a normal procedure (not a continuation)")));
  if (!s7_is_aritable(sc, lessp, 2))
    return(wrong_type_argument_with_type(sc, sc->SORT, small_int(2), lessp, make_protected_string(sc, "a procedure that can take 2 arguments")));

  sort_func = vector_compare;
  compare_func = NULL;
  compare_args = sc->T2_1;
  compare_sc = sc;

  /* (define (hi a) (sort! a (lambda (x y) (< x y))))
   */
  if ((is_safe_procedure(lessp)) &&
      (is_c_function(lessp)))
    compare_func = c_function_call(lessp);
  else
    {
      /* an experiment. other similar cases are assoc/member (and for-each/map but they never happen in this context -- see op_for_each_ls_2 below)
       */
      if ((is_closure(lessp)) &&
	  (is_null(cdr(closure_body(lessp)))) &&
	  (is_optimized(car(closure_body(lessp)))))
	{
	  /* since (sort seq (lambda (a b) ...)) can't return a "safe closure" (the hop bit is off in
	   *   optimize in this case, for some arcane reason), the optimized expression won't be hop_safe,
	   *   but that is irrelevant at this point -- if c_function_is_ok, we're good to go.
	   *   So, treat it as if it were a dox end expression, creating a frame and using dox1|2 for
	   *   any symbols. This way we can handle slightly tricky cases like (lambda (a b) (< (car b) a))
	   *   without any extra work up front.  compare_func needs to be a dox-like-version of c_call(expr)
	   *   where the 2 args are placed in dox1|2 before calling the actual function.
	   */
	  s7_pointer expr, args;
	  s7_function sort_f;
	  expr = car(closure_body(lessp));
	  args = closure_args(lessp);

	  if ((is_pair(args)) &&
	      (!arglist_has_accessed_symbol(args)) &&
	      (((optimize_data(expr) & 1) != 0) ||
	       (c_function_is_ok(sc, expr))))
	    {
	      int orig_data;
	      orig_data = optimize_data(expr);
	      set_optimize_data(expr, optimize_data(expr) | 1);
	      sort_f = end_dox_eval(sc, expr);
	      if (sort_f)
		{
		  NEW_FRAME_WITH_TWO_SLOTS(sc, closure_environment(lessp), sc->envir, car(args), sc->F, cadr(args), sc->F);
		  initialize_dox_end_vars(sc, expr);
		  compare_func = sort_f;
		  sort_func = dox_compare;
		  compare_args = expr;
		}
	      set_optimize_data(expr, orig_data);
	    }
	}
    }

  switch (type(data))
    {
    case T_PAIR:
      len = s7_list_length(sc, data);            /* nil disposed of above, so 0 here == infinite */
      if (len <= 0)
	return(s7_error(sc, sc->WRONG_TYPE_ARG, list_2(sc, make_protected_string(sc, "sort! argument 1 should be a proper list: ~S"), data)));
      if (len < 2) 
	return(data);

      if (compare_func)
	{
	  int gc_loc, i;
	  s7_pointer vec, p;

	  vec = g_vector(sc, data);
	  elements = s7_vector_elements(vec);

	  gc_loc = s7_gc_protect(sc, vec);
	  qsort((void *)elements, len, sizeof(s7_pointer), sort_func);
	  for (p = data, i = 0; i < len; i++, p = cdr(p))
	    car(p) = elements[i];
	  s7_gc_unprotect_at(sc, gc_loc);

	  return(data);
	}

      push_stack(sc, OP_SORT_PAIR_END, cons(sc, data, lessp), sc->code); /* save and gc protect the original list and func */
      car(args) = g_vector(sc, data);
      break;

    case T_STRING:
      {
	int i;
	s7_pointer vec;
	unsigned char *chrs;

	len = string_length(data);
	if (len < 2) return(data);
	vec = make_vector_1(sc, len, NOT_FILLED, T_VECTOR);
	elements = s7_vector_elements(vec);
	chrs = (unsigned char *)string_value(data);

	if (is_bytevector(data))
	  {
	    for (i = 0; i < len; i++)
	      elements[i] = small_int(chrs[i]);
	  }
	else
	  {
	    for (i = 0; i < len; i++)
	      elements[i] = chars[chrs[i]];
	  }

	if (compare_func)
	  {
	    int gc_loc;
	    gc_loc = s7_gc_protect(sc, vec);	  
	    qsort((void *)elements, len, sizeof(s7_pointer), sort_func);
	    
	    if (is_bytevector(data))
	      {
		for (i = 0; i < len; i++)
		  chrs[i] = (char)integer(elements[i]);
	      }
	    else
	      {
		for (i = 0; i < len; i++)
		  chrs[i] = character(elements[i]);
	      }

	    s7_gc_unprotect_at(sc, gc_loc);
	    return(data);
	  }
	
	push_stack(sc, OP_SORT_STRING_END, cons(sc, data, lessp), sc->code);
	car(args) = vec;
      }
      break;

    case T_INT_VECTOR:
    case T_FLOAT_VECTOR:
      {
	int i;
	s7_pointer vec;
	
	/* currently we have to make the ordinary vector here even if not compare_func
	 *   because the sorter uses vector_element to access sort args (see SORT_DATA in eval).
	 *   This is probably better than passing down getter/setter (fewer allocations).
	 *   get/set macro in eval is SORT_DATA(k) then s7_vector_to_list if pair at start (sort_*_end)
	 *
	 * if we know the underlying C function (say < or >), we could greatly optimize this --
	 *   is it worth the effort?  What are the most common cases here -- I expect sort vector of strings.
	 */
	len = vector_length(data);
	if (len < 2) return(data);
	vec = make_vector_1(sc, len, NOT_FILLED, T_VECTOR);
	elements = s7_vector_elements(vec);

	for (i = 0; i < len; i++)
	  elements[i] = vector_getter(data)(sc, data, i);

	if (compare_func)
	  {
	    int gc_loc;
	    gc_loc = s7_gc_protect(sc, vec);	  
	    qsort((void *)elements, len, sizeof(s7_pointer), sort_func);
	    
	    for (i = 0; i < len; i++)
	      vector_setter(data)(sc, data, i, elements[i]);

	    s7_gc_unprotect_at(sc, gc_loc);
	    return(data);
	  }
	
	push_stack(sc, OP_SORT_VECTOR_END, cons(sc, data, lessp), sc->code); /* save and gc protect the original homogenous vector and func */
	car(args) = vec;
      }
      break;

    case T_VECTOR:
      len = vector_length(data);
      if (len < 2) return(data);
      if (compare_func)
	{
	  qsort((void *)s7_vector_elements(data), len, sizeof(s7_pointer), sort_func);
	  return(data);
	}
      break;

    default:
      check_method(sc, data, sc->SORT, args);
      return(wrong_type_argument_with_type(sc, sc->SORT, small_int(1), data, make_protected_string(sc, "a sequence")));
    }

  n = len - 1;
  k = ((int)(n / 2)) + 1;

  sc->x = s7_make_vector(sc, (sc->safety == 0) ? 4 : 6); 
  vector_element(sc->x, 0) = make_mutable_integer(sc, n);
  vector_element(sc->x, 1) = make_mutable_integer(sc, k);
  vector_element(sc->x, 2) = make_mutable_integer(sc, 0);
  vector_element(sc->x, 3) = make_mutable_integer(sc, 0);
  if (sc->safety != 0)
    {
      vector_element(sc->x, 4) = make_mutable_integer(sc, 0);
      vector_element(sc->x, 5) = make_integer(sc, n * n);
    }

  push_stack(sc, OP_SORT, args, sc->x);
  sc->x = sc->NIL;
  return(sc->F);
  
  /* if the comparison function waffles, sort! can hang: (sort! '(1 2 3) (lambda (a b) (= a b)))
   * set *safety* to 1 to add a check for this loop, but the "safe" procedures are direct, so unchecked.
   */
}




/* -------- hash tables -------- */

bool s7_is_hash_table(s7_pointer p)
{
  return(is_hash_table(p));
}


static s7_pointer g_is_hash_table(s7_scheme *sc, s7_pointer args)
{
  #define H_is_hash_table "(hash-table? obj) returns #t if obj is a hash-table"
  check_boolean_method(sc, car(args), is_hash_table, sc->IS_HASH_TABLE, args);
}


static s7_pointer g_hash_table_size(s7_scheme *sc, s7_pointer args)
{
  #define H_hash_table_size "(hash-table-size obj) returns the size of the hash-table obj"
  if (!is_hash_table(car(args)))
    {
      check_method(sc, car(args), sc->HASH_TABLE_SIZE, args);
      return(simple_wrong_type_argument(sc, sc->HASH_TABLE_SIZE, car(args), T_HASH_TABLE));
    }
  return(make_integer(sc, hash_table_length(car(args))));
}


static s7_pointer g_hash_table_entries(s7_scheme *sc, s7_pointer args)
{
  #define H_hash_table_entries "(hash-table-entries obj) returns the number of entries in the hash-table obj"
  if (!is_hash_table(car(args)))
    {
      check_method(sc, car(args), sc->HASH_TABLE_ENTRIES, args);
      return(simple_wrong_type_argument(sc, sc->HASH_TABLE_ENTRIES, car(args), T_HASH_TABLE));
    }
  return(make_integer(sc, hash_table_entries(car(args))));
}


static int hash_float_location(s7_Double x)
{
  int loc;
  if ((is_inf(x)) || (is_NaN(x)))
    return(0);

  x = fabs(x);
  if (x < 100.0)
    loc = 1000.0 * x;
  else loc = x;

  if (loc < 0) /* (hash-table-index 1e+18) -> -2147483648 */
    return(0);

  return(loc);
}


static unsigned int hash_loc(s7_scheme *sc, s7_pointer key)
{
  unsigned int loc = 0;

  switch (type(key))
    {
    case T_STRING:
      if (string_hash(key) != 0)
	return(string_hash(key));
      loc = raw_string_hash((const unsigned char *)string_value(key));
      string_hash(key) = loc;
      return(loc);

    case T_INTEGER:
      if (integer(key) < 0)
	return((unsigned int)(-integer(key)));
      return((unsigned int)integer(key)); /* overflow possible! */

    case T_REAL:
      return(hash_float_location(real(key)));
      /* currently 1e300 goes to most-negative-fixnum! -> 0 after logand size, I hope 
       *
       * we need round, not floor for the location calculation in the real/complex cases else
       *    1-eps doesn't match 1.0, but 1+eps does.  And what if round(val) is too big for int?
       *    lrint is complex and requires special compiler flags to get any speed (-fno-math-errno).
       *    all we need is (int)(val+0.5) -- all the other stuff is pointless in this context
       */

    case T_RATIO:
      loc = (unsigned int)denominator(key); /* overflow possible */
      /* if (loc < 0) return(-loc); */
      return(loc);

    case T_COMPLEX:
      return(hash_float_location(real_part(key)));

    case T_SYMBOL:
      return(symbol_hash(key));

    case T_SYNTAX:
      return(symbol_hash(slot_symbol(global_slot(key))));

    case T_CHARACTER:
      return(character(key));

    case T_HASH_TABLE:
    case T_INT_VECTOR:
    case T_FLOAT_VECTOR:
    case T_VECTOR:
      return(vector_length(key));

    case T_PAIR:
      /* len+loc(car) is not horrible, but it means (for example) every list '(set! a b) is hashed to the same location,
       *   so at least we need to take cadr into account if possible.  Better would combine the list_length(max 5 == safe_strlen5?) call
       *   with stats like symbols/pairs/constants at top level, then use those to spread it out over all the locs.
       */
      {
	s7_pointer p1, p2;
	if (car(key) != key)
	  loc = hash_loc(sc, car(key)) + 1;
	p1 = cdr(key);
	if (is_pair(p1))
	  {
	    if ((car(p1) != key) && (car(p1) != p1))
	      {
		if (is_pair(car(p1)))
		  loc = hash_loc(sc, caar(p1)) + 1 + loc * 2 + s7_list_length(sc, car(p1));
		else loc = hash_loc(sc, car(p1)) + 1 + loc * 2;
	      }
	    p2 = cdr(p1);
	    if (is_pair(p2))
	      {
		if ((car(p2) != key) && (car(p2) != p1) && (car(p2) != p2))
		  {
		    if (is_pair(car(p2)))
		      loc = hash_loc(sc, caar(p2)) + 1 + loc * 2 + s7_list_length(sc, car(p2));
		    else loc = hash_loc(sc, car(p2)) + 1 + loc * 2;
		  }
	      }
	  }
	return(loc);
      }
#if 0
      loc = (int)s7_list_length(sc, key);
      if ((loc > 0) && (key != car(key)))      /* avoid infinite loop if #1=(#1#)! */
	return(loc + hash_loc(sc, car(key)));
      return(0);
#endif

    default:
      break;
    }

  return(type(key));
}


static s7_pointer g_hash_table_index(s7_scheme *sc, s7_pointer args)
{
  return(make_integer(sc, hash_loc(sc, car(args))));
}


static s7_pointer hash_empty(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  return(sc->NIL);
}


static s7_pointer hash_int(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  if (s7_is_integer(key))
    {
      s7_Int keyval;
      s7_pointer x;
      unsigned int loc;
      unsigned long long int hash_len;

      hash_len = (unsigned long long int)hash_table_length(table) - 1;
      keyval = integer(key);
      if (keyval < 0)
	loc = (unsigned int)((-keyval) & hash_len);
      else loc = (unsigned int)(keyval & hash_len);

      for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
	if (integer(fcdr(x)) == keyval)
	  return(car(x));
    }
  return(sc->NIL);
}


static s7_pointer hash_string(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  /* cityhash and spookyhash are the top-of-the-line, but do we need that much power? 
   */
  if (is_string(key))
    {
      s7_pointer x;
      unsigned int hash_len, loc, orig;

      hash_len = (unsigned int)hash_table_length(table) - 1;
      loc = string_hash(key);
      if (loc == 0)
	{
	  loc = raw_string_hash((const unsigned char *)string_value(key));
	  string_hash(key) = loc;
	}
      orig = loc;
      loc &= hash_len;
      for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
	if ((orig == string_hash(fcdr(x))) &&
	    (strings_are_equal(string_value(fcdr(x)), string_value(key))))
	  return(car(x));
    }
  return(sc->NIL);
}


static s7_pointer hash_char(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  if (s7_is_character(key))
    {
      s7_pointer x;
      int hash_len, loc;
      hash_len = (int)hash_table_length(table) - 1;
      loc = character(key) & hash_len;
      for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
	if (fcdr(x) == key)
	  return(car(x));
    }
  return(sc->NIL);
}


#define HASH_FLOAT_EPSILON 1.0e-12

static s7_pointer hash_float_1(s7_scheme *sc, s7_pointer table, int loc, s7_Double keyval)
{
  s7_pointer x;
  bool look_for_nan;
  look_for_nan = is_NaN(keyval);
  
  for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
    {
      s7_pointer y;
      y = fcdr(x);
      if (type(y) == T_REAL) /* we're possibly called from hash_equal, so keys might not be T_REAL */
	{
	  s7_Double val;
	  val = real(y);
	  if (look_for_nan)
	    {
	      if (is_NaN(val))
		return(car(x));
	    }
	  else
	    {
	      if ((val == keyval) ||   /* inf case */
		  (fabs(val - keyval) < HASH_FLOAT_EPSILON))
		return(car(x));
	    }
	}
    }
  return(sc->NIL);
}


static s7_pointer hash_float(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  /* give the equality check some room.  We only get here if key is T_REAL.
   *   also inf == inf and nan == nan
   */
  s7_Double keyval;
  int hash_len, loc;

  hash_len = (int)hash_table_length(table) - 1;
  keyval = real(key);
  loc = hash_float_location(keyval) & hash_len;

  return(hash_float_1(sc, table, loc, keyval));
}


static s7_pointer hash_complex_1(s7_scheme *sc, s7_pointer table, int loc, s7_pointer key)
{
  s7_pointer x;
  for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
    {
      s7_pointer y;
      y = fcdr(x);
      if ((type(y) == T_COMPLEX) &&
	  (s7_is_morally_equal_1(sc, y, key, NULL)))
	return(car(x));
    }
  return(sc->NIL);
}


static s7_pointer hash_equal(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  /* there is a problem here if we hash mixed numeric types and floats are compared using float-epsilon.
   *   to remain consistent even in mixed cases, we need to trap the floats here before s7_is_equal.
   *   complex numbers are similar.
   *
   * morally-equal? can't be used here unless we use it even in hash_int and other such cases.
   *   otherwise, what matches depends on what else is in the hash, and on the order in which 
   *   they were placed there.  But I think the key 1 should not match the key 1.0+epsilon,
   *   and similarly for ratios, yet NaN should definitely match NaN (else it's a nutty no-op
   *   to use such a key). 
   *
   * yet another problem: hashing a closure can't work.  A closure is not necessarily equal to itself.
   */
  s7_pointer x;
  unsigned int hash_len, loc;

  hash_len = (int)hash_table_length(table) - 1;
  loc = hash_loc(sc, key) & hash_len;

  switch (type(key))
    {
    case T_REAL:
      return(hash_float_1(sc, table, loc, real(key)));

    case T_COMPLEX:
      return(hash_complex_1(sc, table, loc, key));

    case T_SYNTAX:
      for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
	if ((global_slot(fcdr(x))) && 
	    (slot_symbol(global_slot(fcdr(x))) == slot_symbol(global_slot(key))))
	  return(car(x));
      break;

    case T_HASH_TABLE:
    case T_SYMBOL:
    case T_CHARACTER:
      for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
	if (fcdr(x) == key)
	  return(car(x));
      break;
      
    default:
      /* we can get into an infinite loop here, but it requires 2 hash tables that are members of each other
       *    and key is one of them, so I changed the equality check above to use eq? -- not sure this is right.
       */
      /* hope for an easy case... */
      for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
	if (fcdr(x) == key)
	  return(car(x));

      for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
	if (s7_is_equal(sc, fcdr(x), key))
	  return(car(x));
    }
  return(sc->NIL);
}


static s7_pointer hash_eq_func(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  s7_pointer x;
  unsigned int hash_len, loc;
  s7_function f;

  f = c_function_call(hash_table_eq_function(table));
  hash_len = (unsigned int)hash_table_length(table) - 1;
  loc = hash_loc(sc, key) & hash_len;

  car(sc->T2_1) = key;
  for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
    {
      car(sc->T2_2) = fcdr(x);
      if (is_true(sc, (*f)(sc, sc->T2_1)))
	return(car(x));
    }
  return(sc->NIL);
}


static s7_pointer hash_symbol(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  if (is_symbol(key))
    {
      s7_pointer x;
      int hash_len, loc;
      hash_len = (int)hash_table_length(table) - 1;
      loc = symbol_hash(key) & hash_len;
      for (x = hash_table_elements(table)[loc]; is_pair(x); x = cdr(x))
	if (fcdr(x) == key)
	  return(car(x));
    }
  return(sc->NIL);
}


#define DEFAULT_HASH_TABLE_SIZE 511
/* this may be too big -- smaller is faster in lint (even 31), but not in make-index.  Perhaps we need *hash-table-default-size*?
 *   another approach: bi-level tables where we index twice, and thereby skip over (sqrt n) (or whatever) at a time.  
 *   does this happen often? would it matter in the gc enough to warrant the code?
 * or perhaps combine the back-and-forth indexing to a hash-table, so we'd only have the sections we need
 *
 * also what about a simplified hash_table with just the keys => a set, similar to the memq/assq difference
 *   we could add T_SET just like T_BYTEVECTOR and specialize where necessary.
 *   vector->set->hash-table->environment->library
 */

s7_pointer s7_make_hash_table(s7_scheme *sc, s7_Int size)
{
  s7_pointer table;
  int i;
  /* size is rounded up to the next power of 2 */

  if ((size & (size + 1)) != 0)      /* already 2^n - 1 ? */
    {
      size--;
      size |= (size >> 1);
      size |= (size >> 2);
      size |= (size >> 4);
      size |= (size >> 8);
      size |= (size >> 16);
      if (s7_int_bits > 31) /* this is either 31 or 63 */
	size |= (size >> 32);
    }
  size++;

  NEW_CELL(sc, table);
  hash_table_length(table) = size;
  hash_table_elements(table) = (s7_pointer *)malloc(size * sizeof(s7_pointer));
  if (!(hash_table_elements(table)))
    return(s7_error(sc, make_symbol(sc, "out-of-memory"), 
		    list_1(sc, make_protected_string(sc, "make-hash-table allocation failed!"))));
  for (i = 0; i < size; i++)
    hash_table_element(table, i) = sc->NIL;
  /* or use the vector fill stuff here, but that now just unrolls the loop */
  hash_table_function(table) = hash_empty;
  hash_table_eq_function(table) = NULL;
  hash_table_entries(table) = 0;
  set_type(table, T_HASH_TABLE | T_SAFE_PROCEDURE);
  add_hash_table(sc, table);

  return(table);
}


static s7_pointer g_make_hash_table(s7_scheme *sc, s7_pointer args)
{
  #define H_make_hash_table "(make-hash-table (size 511) eq-func) or (make-hash-table eq-func (size 511)) returns a new hash table"
  s7_Int size = DEFAULT_HASH_TABLE_SIZE;

  if (is_not_null(args))
    {
      s7_pointer size_arg, eq_arg;
      if (s7_is_integer(car(args)))
	{
	  size_arg = car(args);
	  if (is_not_null(cdr(args)))
	    eq_arg = cadr(args);
	  else eq_arg = sc->GC_NIL;
	}
      else
	{
	  eq_arg = car(args);
	  if (is_not_null(cdr(args)))
	    {
	      size_arg = cadr(args);
	      if (!s7_is_integer(size_arg))
		return(simple_wrong_type_argument(sc, sc->MAKE_HASH_TABLE, size_arg, T_INTEGER));
	    }
	  else size_arg = sc->GC_NIL;
	}

      if (size_arg != sc->GC_NIL)
	{
	  size = s7_integer(size_arg);
	  if (size <= 0)                      /* we need s7_Int here to catch (make-hash-table most-negative-fixnum) etc */
	    return(simple_out_of_range(sc, sc->MAKE_HASH_TABLE, size_arg, "should be a positive integer"));
	  if (size > MAX_LIST_LENGTH)
	    return(simple_out_of_range(sc, sc->MAKE_HASH_TABLE, size_arg, "should be a reasonable integer"));
	}

      if (eq_arg != sc->GC_NIL)
	{
	  s7_pointer ht;
	  int typ;
	  typ = type(eq_arg);
	  /* true procedure needed here else (make-hash-table ()) will work
	   */
	  if ((typ < T_C_FUNCTION) && (typ != T_CLOSURE) && (typ != T_CLOSURE_STAR)) 
	    {
	      check_method(sc, eq_arg, sc->MAKE_HASH_TABLE, args);
	      return(simple_wrong_type_argument_with_type(sc, sc->MAKE_HASH_TABLE, eq_arg, A_PROCEDURE));
	    }
	  /* now try to deal with eq_arg -> hash_table_eq_func[tion]
	   */
	  if (is_c_function(eq_arg)) /* we need to restrict this function so that hash-table-ref|set! remain safe functions */
	    {
	      ht = s7_make_hash_table(sc, size);
	      hash_table_function(ht) = hash_eq_func;
	      hash_table_eq_function(ht) = eq_arg;
	      return(ht);
	    }
	}
    }
  
  return(s7_make_hash_table(sc, size));
}


s7_pointer s7_hash_table_ref(s7_scheme *sc, s7_pointer table, s7_pointer key)
{
  s7_pointer x;
  x = (*hash_table_function(table))(sc, table, key);

  if (is_not_null(x))
    return(cdr(x));
  return(sc->F);
}


s7_pointer s7_hash_table_set(s7_scheme *sc, s7_pointer table, s7_pointer key, s7_pointer value)
{
  s7_pointer x;
  x = (*hash_table_function(table))(sc, table, key);

  if (is_not_null(x))
    cdr(x) = value;
  else
    {
      unsigned int hash_len, loc;
      int typ;

      hash_len = hash_table_length(table) - 1;
      loc = hash_loc(sc, key) & hash_len;
      hash_table_entries(table)++;

      if (hash_table_function(table) != hash_eq_func)
	{
	  typ = type(key);
	  if (hash_table_function(table) == hash_empty)
	    {
	      switch (typ)
		{
		case T_STRING:
		  hash_table_function(table) = hash_string;
		  break;
		  
		case T_INTEGER:
		  hash_table_function(table) = hash_int;
		  break;
		  
		case T_REAL:
		  hash_table_function(table) = hash_float;
		  break;
		  
		case T_RATIO:
		case T_COMPLEX:
		  hash_table_function(table) = hash_equal;
		  break;
		  
		case T_SYMBOL:
		  hash_table_function(table) = hash_symbol;
		  break;
		  
		case T_CHARACTER:
		  hash_table_function(table) = hash_char;
		  break;
		  
		default:
		  hash_table_function(table) = hash_equal;
		  /* if every key structure is simple, we'd like to use a simple_equal checker (no circles),
		   *    but circles can sneak in!  lists (etc) are dangerous keys:
		   *
		   (let ((ht (make-hash-table))
		         (lst1 (list 1 2))
		         (lst2 (list 1 2)))
		     (set! (ht lst1) 32)
		     (let ((start (ht lst2)))
		       (set! (lst1 0) 3)
		       (list start (ht lst2))))
		   (32 #f)
		   *
		   * but this applies to all such variables, even strings.  Do other schemes copy the key?
		   */
		  break;
		}
	    }
	  else
	    {
	      switch (typ)
		{
		case T_STRING:
		  if (hash_table_function(table) != hash_string)
		    hash_table_function(table) = hash_equal;
		  break;
		  
		case T_INTEGER:
		  if (hash_table_function(table) != hash_int)
		    hash_table_function(table) = hash_equal;
		  break;
		  
		case T_REAL:
		  if (hash_table_function(table) != hash_float)
		    hash_table_function(table) = hash_equal;
		  break;
		  
		case T_RATIO:
		case T_COMPLEX:
		  hash_table_function(table) = hash_equal;
		  break;
		  
		case T_CHARACTER:
		  if (hash_table_function(table) != hash_char)
		    hash_table_function(table) = hash_equal;
		  break;
		  
		case T_SYMBOL:
		  if (hash_table_function(table) != hash_symbol)
		    hash_table_function(table) = hash_equal;
		  break;
		  
		default:
		  hash_table_function(table) = hash_equal;
		  break;
		}
	    }
	}

      x = cons_unchecked(sc, cons(sc, key, value), hash_table_elements(table)[loc]);
      hash_table_elements(table)[loc] = x;
      set_fcdr(x, key);
    }
  return(value);
}


static s7_pointer g_hash_table_ref(s7_scheme *sc, s7_pointer args)
{
  #define H_hash_table_ref "(hash-table-ref table key) returns the value associated with key in the hash table"
  s7_pointer table;

  table = car(args);
  
  if (!is_hash_table(table))
    {
      check_method(sc, table, sc->HASH_TABLE_REF, args);
      return(wrong_type_argument(sc, sc->HASH_TABLE_REF, small_int(1), table, T_HASH_TABLE));
    }
  /*
    (define (href H . args) 
      (if (null? (cdr args))
          (hash-table-ref H (car args))
          (apply href (hash-table-ref H (car args)) (cdr args))))
  */

  if (is_null(cddr(args)))
    return(s7_hash_table_ref(sc, table, cadr(args)));
  return(implicit_index(sc, s7_hash_table_ref(sc, table, cadr(args)), cddr(args)));
}


static s7_pointer hash_table_ref_2;
static s7_pointer g_hash_table_ref_2(s7_scheme *sc, s7_pointer args)
{
  s7_pointer x, table;
  table = car(args);
  
  if (!is_hash_table(table))
    {
      check_method(sc, table, sc->HASH_TABLE_REF, args);
      return(wrong_type_argument(sc, sc->HASH_TABLE_REF, small_int(1), table, T_HASH_TABLE));
    }
  if (hash_table_entries(table) == 0)
    return(sc->F);

  x = (*hash_table_function(table))(sc, table, cadr(args));
  if (is_not_null(x))
    return(cdr(x));
  return(sc->F);
}

static s7_pointer hash_table_ref_ss;
static s7_pointer g_hash_table_ref_ss(s7_scheme *sc, s7_pointer args)
{
  s7_pointer x, table;
  table = find_symbol_checked(sc, car(args));
  
  if (!is_hash_table(table))
    {
      check_method(sc, table, sc->HASH_TABLE_REF, list_2(sc, table, find_symbol_checked(sc, cadr(args))));
      return(wrong_type_argument(sc, sc->HASH_TABLE_REF, small_int(1), table, T_HASH_TABLE));
    }
  if (hash_table_entries(table) == 0)
    return(sc->F);

  x = (*hash_table_function(table))(sc, table, find_symbol_checked(sc, cadr(args)));
  if (is_not_null(x))
    return(cdr(x));
  return(sc->F);
}

static s7_pointer hash_table_ref_car;
static s7_pointer g_hash_table_ref_car(s7_scheme *sc, s7_pointer args)
{
  s7_pointer x, y, table;
  table = find_symbol_checked(sc, car(args));
  
  if (!is_hash_table(table))
    {
      check_method(sc, table, sc->HASH_TABLE_REF, list_2(sc, table, car(find_symbol_checked(sc, cadadr(args)))));
      return(wrong_type_argument(sc, sc->HASH_TABLE_REF, small_int(1), table, T_HASH_TABLE));
    }
  if (hash_table_entries(table) == 0)
    return(sc->F);

  y = find_symbol_checked(sc, cadadr(args));
  if (!is_pair(y))
    return(simple_wrong_type_argument(sc, sc->CAR, y, T_PAIR));

  x = (*hash_table_function(table))(sc, table, car(y));
  if (is_not_null(x))
    return(cdr(x));
  return(sc->F);
}


static s7_pointer g_hash_table_set(s7_scheme *sc, s7_pointer args)
{
  #define H_hash_table_set "(hash-table-set! table key value) sets the value associated with key in the hash table to value"
  s7_pointer table;

  table = car(args);
  
  if (!is_hash_table(table))
    {
      check_method(sc, table, sc->HASH_TABLE_SET, args);
      return(wrong_type_argument(sc, sc->HASH_TABLE_SET, small_int(1), table, T_HASH_TABLE));
    }
  /* how would (set! (ht a b) c) choose the inner table if (ht a b) is not found?
   *   I'm not sure the multi-index case makes sense here
   */

  return(s7_hash_table_set(sc, table, cadr(args), caddr(args)));
}


static s7_pointer g_hash_table(s7_scheme *sc, s7_pointer args)
{
  #define H_hash_table "(hash-table ...) returns a hash-table containing the cons's passed as its arguments. \
That is, (hash-table '(\"hi\" . 3) (\"ho\" . 32)) returns a new hash-table with the two key/value pairs preinstalled."

  int len, ht_loc;
  s7_pointer x, ht;

  /* this accepts repeated keys: (hash-table '(a . 1) '(a . 1)) -- or just '(a) for that matter
   */

  for (len = 0, x = args; is_pair(x); x = cdr(x), len++)
    if ((!is_pair(car(x))) &&
	(!is_null(car(x))))
      return(wrong_type_argument(sc, sc->HASH_TABLE, make_integer(sc, position_of(x, args)), car(x), T_PAIR));

  ht = s7_make_hash_table(sc, (len > 512) ? 4095 : 511);
  if (len > 0)
    {
      ht_loc = s7_gc_protect(sc, ht); /* hash_table_set can cons, so we need to protect this */
      for (x = args; is_pair(x); x = cdr(x)) 
	{
	  if (is_pair(car(x)))
	    s7_hash_table_set(sc, ht, caar(x), cdar(x));
	}
      s7_gc_unprotect_at(sc, ht_loc);
    }
  return(ht);
}


static s7_pointer hash_list_copy(s7_scheme *sc, s7_pointer obj)
{
  if (is_pair(obj))
    {
      s7_pointer p;
      p = cons(sc, s7_copy(sc, car(obj)), hash_list_copy(sc, cdr(obj)));
      set_fcdr(p, caar(p));
      return(p);
    }
  return(obj);
}


static s7_pointer hash_table_copy(s7_scheme *sc, s7_pointer old_hash)
{
  /* this has to copy not only the lists but the cons's in the lists! */
  int i, len;
  s7_pointer new_hash;
  s7_pointer *old_lists, *new_lists;
  int gc_loc;

  len = hash_table_length(old_hash);
  new_hash = s7_make_hash_table(sc, len);
  gc_loc = s7_gc_protect(sc, new_hash);

  old_lists = hash_table_elements(old_hash);
  new_lists = hash_table_elements(new_hash);

  for (i = 0; i < len; i++)
    if (is_not_null(old_lists[i]))
      new_lists[i] = hash_list_copy(sc, old_lists[i]);

  hash_table_entries(new_hash) = hash_table_entries(old_hash);
  hash_table_function(new_hash) = hash_table_function(old_hash);
  hash_table_eq_function(new_hash) = hash_table_eq_function(old_hash);

  s7_gc_unprotect_at(sc, gc_loc);
  return(new_hash);
}


static s7_pointer hash_table_reverse(s7_scheme *sc, s7_pointer old_hash)
{
  int i, len;
  s7_pointer new_hash;
  s7_pointer *old_lists;
  int gc_loc;

  len = hash_table_length(old_hash);
  new_hash = s7_make_hash_table(sc, len);
  gc_loc = s7_gc_protect(sc, new_hash);

  old_lists = hash_table_elements(old_hash);
  /* don't set entries or function -- s7_hash_table_set below will handle those (really?) */

  for (i = 0; i < len; i++)
    {
      s7_pointer x;
      for (x = old_lists[i]; is_not_null(x); x = cdr(x))
	s7_hash_table_set(sc, new_hash, cdar(x), caar(x));
    }

  if (hash_table_function(old_hash) == hash_eq_func)
    {
      hash_table_function(new_hash) = hash_eq_func;
      hash_table_eq_function(new_hash) = hash_table_eq_function(old_hash);
    }
    
  s7_gc_unprotect_at(sc, gc_loc);
  return(new_hash);
}


static s7_pointer hash_table_clear(s7_scheme *sc, s7_pointer table)
{
  int i, len;

  len = hash_table_length(table);
  for (i = 0; i < len; i++)
    hash_table_element(table, i) = sc->NIL;
  hash_table_entries(table) = 0;
  if (hash_table_function(table) != hash_eq_func)
    hash_table_function(table) = hash_empty;
  return(table);
}



/* ---------------- hash-table iterators ---------------- */

typedef struct {
  s7_pointer table, lst;
  int loc;
  void *next;
} ht_iter;

static ht_iter *ht_iter_free_list = NULL;

static char *print_ht_iter(s7_scheme *sc, void *val)
{
  char *str;
  str = (char *)malloc(32 * sizeof(char));
  snprintf(str, 32, "#<hash-table-iterator>");
  return(str);
}


static void ht_iter_free(void *p)
{
  ht_iter *val = (ht_iter *)p;
  val->next = (void *)ht_iter_free_list;
  ht_iter_free_list = val;
}


static ht_iter *ht_iter_alloc(void)
{
  if (ht_iter_free_list)
    {
      ht_iter *p;
      p = ht_iter_free_list;
      ht_iter_free_list = (ht_iter *)(p->next);
      return(p);
    }
  return((ht_iter *)malloc(sizeof(ht_iter)));
}


static bool equal_ht_iter(void *val1, void *val2)
{
  ht_iter *h1, *h2;
  if (val1 == val2) return(true);
  h1 = (ht_iter *)val1;
  h2 = (ht_iter *)val2;
  return((h1->table == h2->table) &&
	 (h1->loc == h2->loc)&&
	 (h1->lst == h2->lst));
}


static void mark_ht_iter(void *val)
{
  ht_iter *iter = (ht_iter *)val;
  s7_mark_object(iter->table);
  s7_mark_object(iter->lst);
}


static char *write_ht_iter_readably(s7_scheme *sc, void *val)
{
  write_readably_error(sc, "a hash-table-iterator");
  return(NULL);
}


static s7_pointer copy_ht_iter(s7_scheme *sc, s7_pointer obj)
{
  ht_iter *new_iter, *old_iter;
  old_iter = (ht_iter *)s7_object_value(obj);
  
  new_iter = ht_iter_alloc();
  new_iter->lst = old_iter->lst;
  new_iter->table = old_iter->table;
  new_iter->loc = old_iter->loc;

  return(s7_make_object(sc, sc->ht_iter_tag, (void *)new_iter));
}


static s7_pointer g_make_hash_table_iterator(s7_scheme *sc, s7_pointer args)
{
  #define H_make_hash_table_iterator "(make-hash-table-iterator table) returns a function of no arguments that \
returns the next (key . value) pair in the hash-table each time it is called.  When there are no more pairs, it returns nil."

  ht_iter *iter;
  if (!is_hash_table(car(args)))
    {
      check_method(sc, car(args), sc->MAKE_HASH_TABLE_ITERATOR, args);
      return(simple_wrong_type_argument(sc, sc->MAKE_HASH_TABLE_ITERATOR, car(args), T_HASH_TABLE));
    }
  iter = ht_iter_alloc();
  iter->lst = sc->NIL;
  iter->table = car(args);
  iter->loc = -1;

  return(s7_make_object(sc, sc->ht_iter_tag, (void *)iter));
}


static s7_pointer g_is_hash_table_iterator(s7_scheme *sc, s7_pointer args)
{
  #define H_is_hash_table_iterator "(hash-table-iterator? obj) returns #t if obj is a hash-table iterator."
  s7_pointer obj;

  obj = car(args);
  if ((s7_is_object(obj)) && 
      (c_object_type(obj) == sc->ht_iter_tag))
    return(sc->T);

  check_method(sc, obj, sc->IS_HASH_TABLE_ITERATOR, args);
  return(sc->F);
}


static s7_pointer hash_table_iterate(s7_scheme *sc, s7_pointer iterator)
{
  s7_pointer lst, table;
  ht_iter *iter;
  int loc, len;
  s7_pointer *elements;

  iter = (ht_iter *)s7_object_value(iterator);
  lst = iter->lst;
  if (is_pair(lst))
    {
      iter->lst = cdr(lst);
      return(car(lst));
    }

  table = iter->table;
  len = hash_table_length(table);
  elements = hash_table_elements(table);

  for (loc = iter->loc + 1; loc < len;  loc++)
    {
      s7_pointer x;
      x = elements[loc];
      if (is_not_null(x))
	{
	  iter->loc = loc;
	  iter->lst = cdr(x);
	  return(car(x));
	}
    }

  iter->loc = len;
  return(sc->NIL);
}


static s7_pointer ref_ht_iter(s7_scheme *sc, s7_pointer obj, s7_pointer args)
{
  /* don't check args -- it's simpler in map/for-each to pass an int to all objects in this context
   */
  return(hash_table_iterate(sc, obj));
}

/* no obvious meaning for length of a hash-table-iterator, or set!, fill!, and reverse
 */





