static s7_pointer format_allg, format_allg_no_column, format_just_newline;
static s7_pointer g_format_allg(s7_scheme *sc, s7_pointer args)
{
  return(g_format_1(sc, args));
}

static s7_pointer g_format_just_newline(s7_scheme *sc, s7_pointer args)
{
  s7_pointer pt, str;
  bool need_output;
  int tpoint;
  char *new_str;

  pt = car(args);
 
  if (pt == sc->T)
    pt = sc->output_port;
  else
    {
      if ((pt != sc->F) &&              
	  ((!is_output_port(pt)) ||    
	   (port_is_closed(pt))))
	{
	  check_method(sc, pt, sc->FORMAT, args);
	  return(wrong_type_argument_with_type(sc, sc->FORMAT, small_int(1), pt, A_FORMAT_PORT));
	}
    }

  str = cadr(args);
  new_str = (char *)string_value(str);
  tpoint = string_length(str) - 2;

  need_output = !is_output_port(pt);

  if (pt != sc->F)
    {
      if (tpoint > 0)
	{
	  /* new_str[tpoint] = '\0'; */
	  port_write_string(pt)(sc, new_str, tpoint, pt);
	  /* new_str[tpoint] = '~'; */
	}
      port_write_character(pt)(sc, '\n', pt);
    }

  if (need_output)
    {
      if (tpoint == 0)
	return(s7_make_string_with_length(sc, "\n", 1));

      new_str[tpoint] = '\n';
      new_str[tpoint + 1] = '\0';
      str = s7_make_string_with_length(sc, new_str, tpoint + 1);
      new_str[tpoint] = '~';
      new_str[tpoint + 1] = '%';
      return(str);
    }
  return(sc->F);
}


static s7_pointer g_format_allg_no_column(s7_scheme *sc, s7_pointer args)
{
  s7_pointer pt;
  pt = car(args);
 
  if (!((s7_is_boolean(pt)) ||               /* #f or #t */
	((is_output_port(pt)) &&             /* (current-output-port) or call-with-open-file arg, etc */
	 (!port_is_closed(pt)))))
    {
      check_method(sc, pt, sc->FORMAT, args);
      return(wrong_type_argument_with_type(sc, sc->FORMAT, small_int(1), pt, A_FORMAT_PORT));
    }

  return(format_to_port_1(sc, (pt == sc->T) ? sc->output_port : pt, 
			  string_value(cadr(args)), cddr(args), NULL,
			  !is_output_port(pt), /* i.e. is boolean port so we're returning a string */
			  false,
			  string_length(cadr(args))));
}


static s7_pointer format_chooser(s7_scheme *sc, s7_pointer f, int args, s7_pointer expr)
{
  if ((args > 1) &&
      (!is_string(cadr(expr))) &&
      (is_string(caddr(expr))))
    {
      s7_pointer str_arg;
      str_arg = caddr(expr);
      if (args == 2) 
	{
	  int len;
	  const char *p, *orig;

	  orig = (const char *)string_value(str_arg);
	  len = string_length(str_arg);
	  if ((len > 1) &&
	      (orig[len - 1] == '%'))
	    {
	      p = strchr(orig, (int)'~');
	      if ((p) && ((p - orig) == len - 2))
		return(format_just_newline);
	    }
	}

      /* this used to worry about optimized expr and particular cases -- why? I can't find a broken case */
      if (!is_columnizing(string_value(str_arg)))
	return(format_allg_no_column);
      return(format_allg);
    }

  return(f);
}

static s7_pointer is_eq_car, is_eq_car_q, is_eq_caar_q;
static s7_pointer g_is_eq_car(s7_scheme *sc, s7_pointer args) 
{
  s7_pointer lst, val;
  lst = find_symbol_checked(sc, cadar(args));
  val = find_symbol_checked(sc, cadr(args));
  if (!is_pair(lst))
    return(g_is_eq(sc, list_2(sc, g_car(sc, list_1(sc, lst)), val)));
  return(make_boolean(sc, car(lst) == val));
}

static s7_pointer g_is_eq_car_q(s7_scheme *sc, s7_pointer args) 
{
  s7_pointer lst;
  lst = find_symbol_checked(sc, cadar(args));
  if (!is_pair(lst))
    return(g_is_eq(sc, list_2(sc, g_car(sc, list_1(sc, lst)), cadr(cadr(args)))));
  return(make_boolean(sc, car(lst) == cadr(cadr(args))));
}

static s7_pointer g_is_eq_caar_q(s7_scheme *sc, s7_pointer args) 
{
  s7_pointer lst;
  lst = find_symbol_checked(sc, cadar(args));
  if (!is_pair(lst))
    return(g_is_eq(sc, list_2(sc, g_caar(sc, list_1(sc, lst)), cadr(cadr(args)))));
  return(make_boolean(sc, caar(lst) == cadr(cadr(args))));
}

static s7_pointer is_eq_chooser(s7_scheme *sc, s7_pointer f, int args, s7_pointer expr)
{
  if ((is_pair(cadr(expr))) &&
      (is_optimized(cadr(expr))) &&
      (optimize_data(cadr(expr)) == HOP_SAFE_C_S))
    {
      if ((is_symbol(caddr(expr))) &&
	  (c_call(cadr(expr)) == g_car))
	{
	  set_optimize_data(expr, HOP_SAFE_C_C);
	  return(is_eq_car);
	}
      if ((is_pair(caddr(expr))) &&
	  (caaddr(expr) == sc->QUOTE))
	{
	  if (c_call(cadr(expr)) == g_car)
	    {
	      set_optimize_data(expr, HOP_SAFE_C_C);
	      return(is_eq_car_q);
	    }
	  if (c_call(cadr(expr)) == g_caar)
	    {
	      set_optimize_data(expr, HOP_SAFE_C_C);
	      return(is_eq_caar_q);
	    }
	}
    }
  return(f);
}


