static bool optimize_expression(s7_scheme *sc, s7_pointer x, int hop, s7_pointer e);

static s7_pointer collect_collisions(s7_scheme *sc, s7_pointer lst, s7_pointer e)
{
  s7_pointer p;
  sc->w = e;
  for (p = lst; is_pair(p); p = cdr(p))
    {
      if ((is_symbol(car(p))) &&
	  (global_slot(car(p)) != sc->NIL))
	sc->w = cons(sc, add_sym_to_list(sc, car(p)), sc->w);
      else
	{
	  if ((is_pair(car(p))) &&
	      (is_symbol(caar(p))) &&
	      (global_slot(caar(p)) != sc->NIL))
	    sc->w = cons(sc, add_sym_to_list(sc, caar(p)), sc->w);
	}
    }
  return(sc->w);
}

#define is_all_x_op(Op) t_opt_all_x[Op]

static bool is_all_x_safe(s7_scheme *sc, s7_pointer p)
{
  if ((!is_pair(p)) ||
      ((car(p) == sc->QUOTE) &&
       (is_pair(cdr(p)))))   /* (if #t (quote . -1)) */
    return(true);
  /* fprintf(stderr, "%s: %d %s %d\n", DISPLAY(p), is_optimized(p), opt_name(p), is_all_x_op(optimize_data(p))); */
  if (!is_optimized(p))
    return(false);
  return(is_all_x_op(optimize_data(p)));
}


static int all_x_count(s7_pointer x)
{
  int count = 0;
  s7_pointer p;
  for (p = cdr(x); is_pair(p); p = cdr(p))
    if ((is_pair(car(p))) &&
	(is_optimized(car(p))) &&
	(is_all_x_op(optimize_data(car(p)))))
      count++;
  return(count);
}


