		make_closure_without_capture(sc, x, car(y), cdr(y), sc->envir); /* car=args, cdr=code */

		if ((!is_pair(z)) ||
		    (s7_list_length(sc, z) == 0))
		  {
		    sc->value = g_for_each(sc, list_2(sc, x, z));
		    goto START;
		  }
