  s7_eval_c_string(sc, "(define-macro (multiple-value-bind vars expression . body)                            \n\
                           (if (or (symbol? vars) (negative? (length vars)))                                  \n\
                               `((lambda ,vars ,@body) ,expression)                                           \n\
                               `((lambda* (,@vars . ,(gensym)) ,@body) ,expression)))");
